package Javapracticee;

public class Swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 10;

	    int j = 7;
	    
	    int k;
	    k=i;
	    i=j;
	    j=k;
	    
	    // Write your code

	    System.out.println("Value of i ="+i);

	    System.out.println("Value of j ="+j);

	    // Expected Result , i = 7  and j = 10
	}

}
